package basicjava;

public class CharDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char aChar, bChar;
		aChar = 88;
		bChar = 'Y';
		System.out.print("aChar and bChar :");
		System.out.println(aChar + " "+ bChar);
		
		aChar++;
		System.out.println("aChar : " + aChar);
		
	}

}
